const esPalindromo=o=>{o=o.toLowerCase();let a=0,e=o.length-1;for(;a<e;){for(;a<e&&!/[a-z0-9]/.test(o[a]);)a++;for(;a<e&&!/[a-z0-9]/.test(o[e]);)e--;if(o[a]!==o[e])return!1;a++,e--}return!0},resultado=esPalindromo("Anita lava la tina")?"Es un palíndromo":"No es un palíndromo";console.log(resultado);